mapper.select:
SELECT * FROM ( SELECT s.*,
FROM ety_shop s, ety_ticket t WHERE s.id = t.shop_id
and s.industry_id=#{industry, jdbcType=INTEGER}
AND t.type_id=#{type, jdbcType=INTEGER}
AND s.is_shop_verified = 1 AND t.`status` = 1 ORDER BY t.create_time DESC ) tmp GROUP BY id ORDER BY create_time LIMIT #{offset, jdbcType=INTEGER}, #{limit, jdbcType=INTEGER};

SELECT s.*, ,
FROM ety_shop s, ety_ticket t, rel_user_ticket ut WHERE s.id = t.shop_id AND t.ticket_id = ut.ticket_id AND ut.user_id = #{uid, jdbcType=BIGINT}
ORDER BY ut.create_time limit #{limit,jdbcType=INTEGER}, #{offset, jdbcType=INTEGER};

SELECT
  *
FROM
(
    SELECT
        s.*,

    FROM
        ety_shop s,
        ety_ticket t
    WHERE
        s.id = t.shop_id

            and s.industry_id=#{industry, jdbcType=INTEGER}


            AND t.type_id=#{type, jdbcType=INTEGER}

        AND (
        s.longitude >=#{leftTop.longitude, jdbcType=DECIMAL}
        AND s.longitude <= #{rightTop.longitude, jdbcType=DECIMAL}
        )
        AND (
        s.dimensionality >= #{leftBottom.latitude, jdbcType=DECIMAL}
        AND s.dimensionality <= #{leftTop.latitude, jdbcType=DECIMAL}
        )

        AND (
          s.shop_name like "%"#{keyword, jdbcType=VARCHAR}"%"
          OR
          t.ticket_name like "%"#{keyword, jdbcType=VARCHAR}"%"
        )

        AND s.is_shop_verified = 1
        AND t.`status` = 1
    ORDER BY
      t.create_time DESC
) tmp
GROUP BY
id
ORDER BY
create_time
LIMIT 100;

select COUNT(ut.id)
from ety_shop s,ety_ticket t,rel_user_ticket ut
where s.uid=#{uid, jdbcType=BIGINT}
and s.id=t.shop_id
and t.ticket_id=ut.ticket_id

    AND ut.create_time >= #{today, jdbcType=TIMESTAMP};

SELECT DISTINCT(s.id), s.shop_name, s.shop_address, s.icon_id, s.longitude, s.dimensionality, s.profile_picture, s.brief_intro, t.* FROM ety_shop s, rel_user_shop us, ety_ticket t WHERE us.user_id = #{uid, jdbcType=BIGINT} AND s.id = us.shop_id AND t.shop_id = s.id
AND ( s.shop_name like "%"#{keyword, jdbcType=VARCHAR}"%" OR t.ticket_name like "%"#{keyword, jdbcType=VARCHAR}"%" )
ORDER BY us.create_time DESC limit #{offset,jdbcType=INTEGER}, #{limit, jdbcType=INTEGER}

SELECT
COUNT(s.id) AS uread,
s.id, s.shop_name, s.shop_address, s.icon_id, s.longitude, s.dimensionality, s.profile_picture, s.brief_intro, t.* FROM ety_shop s, rel_user_shop us, ety_ticket t WHERE us.user_id = #{uid, jdbcType=BIGINT} AND s.id = us.shop_id AND t.shop_id = s.id
AND t.create_time > #{createTime,jdbcType=TIMESTAMP}
AND t.`status` = 1 AND s.id GROUP BY s.id ORDER BY us.create_time DESC limit #{limit,jdbcType=INTEGER}, #{offset, jdbcType=INTEGER}

select

from ety_shop




        and is_shop_verified = #{verified}




order by create_time desc


    limit #{limit}


    offset #{offset}
