mapper.select:
select

,

from ety_ticket
where shop_id = #{shop_id,jdbcType=BIGINT}

      AND status = #{status, jdbcType=TINYINT}

ORDER BY create_time DESC limit #{page, jdbcType=BIGINT},#{pageSize,jdbcType=BIGINT}

select
COUNT(id)
from ety_ticket
where shop_id = #{shop_id,jdbcType=BIGINT}
ORDER BY create_time DESC
limit #{page, jdbcType=BIGINT},#{pageSize,jdbcType=BIGINT}

select
,
from ety_ticket where shop_id = #{sid,jdbcType=BIGINT} ORDER BY create_time DESC;

select

from (select * from ety_ticket ORDER BY create_time desc) tmp
where `status`=1 and type_id={#type, jdbcType=BIGINT}
ORDER BY create_time DESC limit #{page, jdbcType=BIGINT},#{pageSize,jdbcType=BIGINT}
