mapper.select:
    SELECT
	  COUNT(id)
    FROM
        rel_user_ticket
    WHERE
        user_id = #{uid, jdbcType=BIGINT}
    AND create_time > #{today, jdbcType=TIMESTAMP}
    LIMIT 1;

